USE kssapp;

CREATE TABLE users (
  `id` INT AUTO_INCREMENT PRIMARY KEY,
  `name` VARCHAR(1024) NOT NULL,
  `gender` CHAR(1) CHECK (gender IN ('E', 'K', NULL)),
  `role` VARCHAR(1024),
  `branch` VARCHAR(1024),
  `job` VARCHAR(1024),
  `duty_unit` VARCHAR(1024),
  `phone` VARCHAR(1024),
  `email` VARCHAR(1024)
);

  